import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import { withRouter } from "react-router";
import './Search.scss'
const IOM = require('./LocalsOnly.png');

// import { geolocated } from "react-geolocated";


class Search extends Component {
  
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: '',
          location: ''
        };
    
        this.handleInput = this.handleInput.bind(this);
        
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
    
      handleInput(event){
        const target = event.target;
        this.setState({
          [target.name]: target.value
        });
      }
    
      
    
      handleSubmit(event){
          event.preventDefault();
          
          if (this.state.what !== '' && this.state.where !== '') 
            this.props.history.push( `/search?what=${this.state.what}&where=${this.state.where}`);
          
        else if (this.state.what !== '' && this.state.where === '') //what is inputted where is empty
          this.props.history.push(`/search?what=${this.state.what}&where=currentZip`);
        else if(this.state.what === '' && this.state.where !== '')  //what is empty where is inputted
        this.props.history.push( `/search?what=all&where=${this.state.where}`);
        else                                                        //what and where are empty
        this.props.history.push( "search?what=all&where=currentZip");
        this.forceUpdate();
        
      }
      
    render() {
     
        return (
          <nav>
            <Link to='/'><img src={IOM} alt="locals-only"/></Link>
            <form onSubmit={this.handleSubmit}>
              <div className="field">
                <div className="control what">
                  <input 
                    placeholder="What to do?" 
                    type="text" 
                    name="what"
                    className="input" 
                    value={this.state.what} 
                    onChange={this.handleInput}
                  />
                </div>
                <div className="control where">
                  <input 
                    placeholder="Where at?"
                    type="text" 
                    name="where"
                    className="input" 
                    value={this.state.where} 
                    onChange={this.handleInput}
                  />
                </div>
                {/* <Link className="button is-primary search" to={this.state.location} >
                  <input type="submit" value="Submit"/>
                </Link> */}
                <button className="button is-primary search">Submit</button>
              </div>
            </form>
            <Link className="button is-primary create" to="/create-local" > 
              <i>Localize it!</i>
            </Link>
          </nav>
        )
    }
}

export default withRouter(Search);
