import React, { Component } from 'react'
import axios from 'axios';
import {DB} from '../globals';
import ResultsCard from '../Components/ResultsCard';
import './results.scss';
import queryString from 'query-string';
import '../../node_modules/loaders.css/';
export default class Results extends Component {
    constructor(props){
        
        super(props);
        this.state = {
            what: '',
            where: '',
            items: [],
            isLoaded: false,
            
        }
    }
    
    componentDidMount(){
        let values = queryString.parse(this.props.location.search);
        console.log(values);
        
        this.setState({ 
                what:values.what,
                where:values.where 
            }
        )
        
        this.getData();
        
    }
    componentDidUpdate(){
        let values = queryString.parse(this.props.location.search);
        console.log(values);
        if(this.state.what !== values.what || this.state.where !== values.where){
            this.setState({ 
                    what:values.what,
                    where:values.where,
                    isLoaded:false
                }
            )
            window.location.reload();
        }
    }
    getData(){
        // if(!this.state.isLoaded){
            axios.get(`${DB}locals`)
            .then((res)=>{
                    console.log("Postal courier has delivered your package!");
                    const data = res.data;
                    this.setState({ items:data, isLoaded: true });
                }
            )
            .catch(()=>{console.log("Postal courier has vanished!");});
        // }
    }
    displayItems(posts){
        if(!posts.length) return null;
        return posts.map((post, index)=>(
            <ResultsCard 
                index={index}
                name={post.name} 
                description={post.description}
                
                />
        ));
    }
    /* <div className="results">
    <div className="items">{this.displayItems(this.state.items)}</div>
    </div> */
    // {!this.state.isLoaded ? <div class="loader-inner ball-pulse"></div> 
    //             : this.displayItems(this.state.items)}
    render() {
        
        return (
            
            <div className="results">
            
            <div className="loader-inner ball-pulse"></div>
                
                
            </div>
            
        )
    }
}
