import React, { Component } from 'react'

export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      what: ' ', 
      where: ' '
    };

    this.handleChangeWhat = this.handleChangeWhat.bind(this);
    this.handleChangeWhere = this.handleChangeWhat.bind(this);
  }


  handleChangeWhat(event){
    this.setState({
      what: event.target.value
    });
  }

  handleChangeWhere(event){
    this.setState({
      where:event.target.value
    });
  }

  onClick(){
    console.log(this.state.what + " " + this.state.where);
  }

  render() {
    return (
      <div>
        <h1>Locals Only</h1>
        <h3>See what Locals are in to!</h3>
        <form>
          <input type="text" placeHolder="what?" 
            id="what-search" 
            value={this.state.what}
            onChange={this.handleChangeWhat}/>
          <input type="text" placeHolder="where?" 
            id="where-search" 
            value={this.state.where} 
            onChange={this.handleChangeWhere}/>
          <input type="button" value="Search" onClick={this.handleOnClick}/>
        </form>
      </div>
    )
  }
}
