import React, { Component } from 'react'
import axios from 'axios';
import {Redirect} from 'react'
import { DB } from '../globals';
export default class newLocal extends Component {
    constructor(props){
        super(props);
        this.state={
            name:'', 
            description:'',
            address:'',
        }

        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeDescription = this.handleChangeDescription.bind(this);
        this.handleChangeAddress = this.handleChangeAddress.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeName(event){
        this.setState({
            name: event.target.value,
        });
    }

    handleChangeDescription(event){
        this.setState({
            description: event.target.value,
        });
    }

    handleChangeAddress(event){
        this.setState({
            address: event.target.value,
        });
    }

    handleSubmit(event){
        event.preventDefault();
        
        axios.post(`${DB}locals`,{
            name: this.state.name,
            description: this.state.description,
            address: this.state.address
        })
            .then(res=>{
                alert("New Local Added");

            })
            .catch(err=>{console.log(err);});
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="name" id="" 
                        value={this.state.name} onChange={this.handleChangeName}/>

                    <input type="text" name="description" id="" 
                        value={this.state.description} onChange={this.handleChangeDescription}/>

                    <input type="text" name="address" id="" 
                        value={this.state.address} onChange={this.handleChangeAddress}/>

                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}
