import React, { Component } from 'react'
import axios from 'axios';
import {TEST} from '../globals';
import ResultsCard from '../Components/ResultsCard';
import './results.scss';
import queryString from 'query-string';
import ClimbingBoxLoader
from "react-spinners/ClimbingBoxLoader";

// const override = css`
//   display: block;
//   margin: 0 auto;
//   border-color: red;
//   padding-top: 2em;
// `;
export default class Results extends Component {
    constructor(props){
        
        super(props);
        this.state = {
            what: '',
            where: '',
            items: [],
            loading: true,
            
        }
    }
    
    componentDidMount(){
        let values = queryString.parse(this.props.location.search);
        console.log(values);
        
        this.setState({ 
                what:values.what,
                where:values.where 
            }
        )
        
        this.getData();
        
    }
    componentDidUpdate(){
        let values = queryString.parse(this.props.location.search);
        console.log(values);
        if(this.state.what !== values.what || this.state.where !== values.where){
            this.setState({ 
                    what:values.what,
                    where:values.where,
                    loading:true
                }
            )
            window.location.reload();
        }
    }
    getData(){
            axios.get(`${TEST}`)
            .then((res)=>{
                    console.log("Postal courier has delivered your package!");
                    const data = res.data;
                    this.setState({ items:data, loading: false });
                }
            )
            .catch(()=>{console.log("Postal courier has vanished!");});
    }
    displayItems(posts){
        if(!posts.length) return null;
        return posts.map((post, index)=>(
            <ResultsCard 
                index={index}
                id={post._id}
                name={post.name} 
                description={post.description}
                rating={post.rating}
                />
        ));
    }
    
    /* <div className="results">
    <div className="items">{this.displayItems(this.state.items)}</div>
    </div> */
    // {!this.state.isLoaded ? <div class="loader-inner ball-pulse"></div> 
    //             : this.displayItems(this.state.items)}
    render() {
        
        return (
            
            <div className="results">
                {this.state.loading? <div className="loading">
                    <ClimbingBoxLoader
                        size={50}
                        color={"#000000"}
                        loading={this.state.loading}
                    />
                </div>:
                this.displayItems(this.state.items)}
                <p>{this.state.items.id}</p>
            </div>
            
        )
    }
}
