import React, { Component } from 'react'
import {Redirect} from 'react-router-dom';
export default class Search extends Component {
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: '',
          click: false
        };
    
        this.handleChangeWhat = this.handleChangeWhat.bind(this);
        this.handleChangeWhere = this.handleChangeWhere.bind(this);
        this.onClick = this.onClick.bind(this);
      }
    
    
      handleChangeWhat(event){
        this.setState({
          what: event.target.value
        });
      }
    
      handleChangeWhere(event){
        this.setState({
          where: event.target.value
        });
      }
    
      onClick(){
          this.setState({
              click: true
          })
      }
    render() {
        if(this.state.what !== '' && this.state.where !== '' && this.state.clicked === true){
            <Redirect to={`/search/Hello/World`}/>
        }
        return (
            <form>
                <input type="text" placeholder="what?" 
                id="what-search" 
                value={this.state.what}
                onChange={this.handleChangeWhat}/>

                <input type="text" placeholder="where?" 
                id="where-search" 
                value={this.state.where}
                onChange={this.handleChangeWhere}/>

                <input type="button" value="Search" onClick={this.onClick}/>
            </form>
        )
    }
}
