import React, { Component } from 'react'
import axios from 'axios';
import { DB } from '../../globals';
import './newLocal.scss';
import StarRatings from 'react-star-ratings';
export default class newLocal extends Component {
    constructor(props){
        super(props);
        this.state={
            name:'', 
            description:'',
            address:{
                street : '',
                apt : '',
                city:'',
                state:'',
                zip:'',
            },
            hashtags:[],
            rating:1,
        }

        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeDescription = this.handleChangeDescription.bind(this);
        this.handleChangeAddress = this.handleChangeAddress.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.changeRating = this.changeRating.bind(this);
    }

    handleChangeName(event){
        this.setState({
            name: event.target.value,
        });
    }

    handleChangeDescription(event){
        this.setState({
            description: event.target.value,
        });
    }

    handleChangeAddress(event){
        this.setState({
            address: event.target.value,
        });
    }

    handleSubmit(event){
        event.preventDefault();
        
        axios.post(`${DB}locals`,{
            name: this.state.name,
            description: this.state.description,
            address: this.state.address,
            hashtags: [this.state.name],
            rating: this.state.rating
        })
            .then(()=>{
                alert("New Local Added");
                this.props.history.push('/');
            })
            .catch(err=>{console.log(err);});
    }
    changeRating( newRating, name ) {
      this.setState({
        rating: newRating
      });
    }
    render() {
        return (
            <div>
            
                <form className="form-add" onSubmit={this.handleSubmit}>
                    <div className="field">
                        <label className="label">Local</label>
                        <div className="control">
                            <input name="" placeholder="Name of Local" type="text"  className="input" value={this.state.name} onChange={this.handleChangeName}/>
                            <p className="help is-danger">This field is required</p>
                        </div>
                   
                        
                            <label className="label">Address</label>
                            <div className="control addr">
                                <label className="label">Street</label>
                                <input type="text" className="input" value={this.state.street} onChange={this.handleChangeAddress}/>
                                <label className="label">Apt/Bld #</label>
                                <input type="text" className="input" value={this.state.apt} onChange={this.handleChangeAddress}/>
                                <label className="label">City/Town</label>
                                <input type="text" className="input" value={this.state.city} onChange={this.handleChangeAddress}/>
                                <label className="label">State</label>
                                <input type="text" className="input" value={this.state.state} onChange={this.handleChangeAddress}/>
                                <label className="label">Postal Code</label>
                                <input type="text" className="input" value={this.state.zip} onChange={this.handleChangeAddress}/>
                                <p className="help is-danger">This field is required</p>
                            </div>
                        
                    
                        <label className="label">Description</label>
                        <div className="control">
                            <textarea className="input" id="desc" value={this.state.description} onChange={this.handleChangeDescription}/>
                            <p className="help is-danger">This field is required</p>
                        </div>
                        <StarRatings
                            rating={this.state.rating}
                            starRatedColor="red"
                            changeRating={this.changeRating}
                            numberOfStars={5}
                            name='rating'
                        />
                    </div>
                    <button className="button is-success"type="submit">Submit</button>
                </form>
            </div>
        )
    }
}
