import React, { Component } from 'react';
import axios from 'axios';
import {TEST} from '../../globals';
import queryString from 'query-string';
export default class Local extends Component {
    constructor(props){
        super(props);
        this.state = {
            id:'',
            items:[],
            loading:true
        }
    }
    componentDidMount() {
        let id = queryString.parse(this.props.location.search).id;
        console.log(id);
        
        this.setState({ 
                id:id,
            }
        )
        console.log(this.state);
        
        this.getData();
    }
    getData(){
        console.log("http://localhost:5000/locals/id/" + this.state.id);
        
        axios.get(`http://localhost:5000/locals/id/${this.state.id}`)
            .then((res)=>{
                    console.log("Postal courier has delivered your package!");
                    const data = res.data;
                    console.log(data);
                    
                    this.setState({ 
                            items:data, 
                            loading: false 
                        }
                    );
                }
            )
            .catch(()=>{console.log("Postal courier has vanished!");});
    }
    loadItems(){
        return this.state.items.name;
    }
    render() {
        return (
            <div className="local">
                {this.state.items._id}
            </div>
        )
    }
}
