import React, { Component } from 'react'
import axios from 'axios';
import { DB } from '../../globals';
import './newLocal.scss';
import Search from '../../Components/Search';
export default class newLocal extends Component {
    constructor(props){
        super(props);
        this.state={
            name:'', 
            description:'',
            address:'',
            hashtags:[],
        }

        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangeDescription = this.handleChangeDescription.bind(this);
        this.handleChangeAddress = this.handleChangeAddress.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeName(event){
        this.setState({
            name: event.target.value,
        });
    }

    handleChangeDescription(event){
        this.setState({
            description: event.target.value,
        });
    }

    handleChangeAddress(event){
        this.setState({
            address: event.target.value,
        });
    }

    handleSubmit(event){
        event.preventDefault();
        
        axios.post(`${DB}locals`,{
            name: this.state.name,
            description: this.state.description,
            address: this.state.address,
            hashtags: [this.state.name]
        })
            .then(()=>{
                alert("New Local Added");
                this.props.history.push('/');
            })
            .catch(err=>{console.log(err);});
    }

    render() {
        return (
            <div>
            
                <form className="form-add" onSubmit={this.handleSubmit}>
                    <div className="field">
                        <label className="label">Local</label>
                        <div className="control">
                            <input placeholder="Name of Local" type="text"  className="input" value={this.state.name} onChange={this.handleChangeName}/>
                            <p className="help is-danger">This field is required</p>
                        </div>
                    </div>

                    <div className="feild">
                        <label className="label">Description</label>
                        <div className="control">
                            <input type="text" className="input" value={this.state.description} onChange={this.handleChangeDescription}/>
                            <p className="help is-danger">This field is required</p>
                        </div>
                    </div>

                    <div className="field">
                        <label className="label">
                            <label className="label">Address</label>
                            <div className="control">
                            <input type="text" className="input" value={this.state.address} onChange={this.handleChangeAddress}/>
                            <p className="help is-danger">This field is required</p>
                            </div>
                        </label>
                    </div>

                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}
