import React, { Component } from 'react'
import {Link} from 'react-router-dom';

import './Search.scss'
const IOM = require('./LocalsOnly.png');

// import { geolocated } from "react-geolocated";


export default class Search extends Component {
  
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: '',
          location: ''
        };
    
        this.handleInput = this.handleInput.bind(this);
        
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
    
      handleInput(event){
        const target = event.target;
        this.setState({
          [target.name]: target.value
        });
      }
    
      
    
      handleSubmit(){
          var location;
          console.log('clicked');
          
          if (this.state.what !== '' && this.state.where !== '')  {
            this.props.push( `/search?what=${this.state.what}&where=${this.state.where}`);
          }
        else if (this.state.what !== '' && this.state.where === '') //what is inputted where is empty
        location = `/search?what=${this.state.what}&where=currentZip`;
        else if(this.state.what === '' && this.state.where !== '')  //what is empty where is inputted
        location = `/search?what=all&where=${this.state.where}`;
        else                                                        //what and where are empty
        location = "search?what=all&where=currentZip"
        this.setState({location:location});
        console.log(this.state.location);
        
      }
      sendData = (what, where)=>{
        this.props.parentCallback(what, where);
      }
    render() {
     
        return (
          <nav>
            <Link to='/'><img src={IOM} alt="locals-only"/></Link>
            <form onSubmit={this.handleSubmit}>
              <div className="field">
                <div className="control what">
                  <input 
                    placeholder="What to do?" 
                    type="text" 
                    name="what"
                    className="input" 
                    value={this.state.what} 
                    onChange={this.handleInput}
                  />
                </div>
                <div className="control where">
                  <input 
                    placeholder="Where at?"
                    type="text" 
                    name="where"
                    className="input" 
                    value={this.state.where} 
                    onChange={this.handleInput}
                  />
                </div>
                <Link className="button is-primary search" to={this.state.location} >
                  <input type="submit" value="Submit"/>
                </Link>
              </div>
            </form>
            <Link className="button is-primary create" to="/create-local" > 
              <i>Localize it!</i>
            </Link>
          </nav>
        )
    }
}
