import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import './Search.scss'
const IOM = require('./LocalsOnly.png');
// import { geolocated } from "react-geolocated";


export default class Search extends Component {
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: ''
        };
    
        this.handleChangeWhat = this.handleChangeWhat.bind(this);
        this.handleChangeWhere = this.handleChangeWhere.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onClick = this.onClick.bind(this);
      }
    
      onClick(){
        this.props.history.push('/');
      }
      handleChangeWhat(event){
        this.setState({
          what: event.target.value
        });
      }
    
      handleChangeWhere(event){
        this.setState({
          where: event.target.value
        });
      }
    
      handleSubmit(){
        
        if (this.state.what !== '' && this.state.where !== '')      //what and where are inputted
          return `/search/${this.state.what}/${this.state.where}`;
        else if (this.state.what !== '' && this.state.where === '') //what is inputted where is empty
          return `search/${this.state.what}/currentZip`;
        else if(this.state.what === '' && this.state.where !== '')  //what is empty where is inputted
          return `search/all/${this.state.where}`;
        else                                                        //what and where are empty
          return `search/all/currentZip`;
        
      }
    render() {
        return (
          <nav>
          <img src={IOM} alt="locals-only" onClick={this.onClick}/>
            
                <div className="field">
                  
                  <div className="control what">
                    <input placeholder="What to do?" type="text" className="input" value={this.state.what} onChange={this.handleChangeWhat}/>
                  </div>
                  
                  <div className="control where">
                    <input placeholder="Where at?"type="text" className="input" value={this.state.where} onChange={this.handleChangewhere}/>
                  </div>
                  <Link className="button is-primary search" to={this.handleSubmit}>Submit</Link>
                  <Link className="button is-primary create" to="/create-local">Create New Local</Link>
                </div>

                
           
            </nav>
        )
    }
}
