// import { geolocated } from "react-geolocated";
// import axios from 'axios';

import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import { withRouter } from "react-router";
import './Search.scss'
const geolib = require('geolib');
const usZips = require('us-zips')
const IOM = require('./LocalsOnly.png');

// const NodeGeocoder = require('node-geocoder');
// const options = {
//   provider:'google',
//   fetch: async (res)=>{console.log(res);},
//   apiKey: "AIzaSyDOZ6d6M5cnuKqBL4IyYXKu7TU_Rt-POFw",
//   formatter:null
// }
// const geocoder = NodeGeocoder(options);
// const res = await geocoder.geocode('1150 W. University Dr., Tempe Az');





class Search extends Component {
  
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: '',
          address:''
        };
    
        this.handleInput = this.handleInput.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
      async componentDidMount() {
        await this.getLocation();
        console.log(this.state.address);
      }
      // {lat:33.597332, lng:-117.194943}
      async getLocation(){
        var loc = await this.props.coords ? {lat:this.props.coords.latitude, lng:this.props.coords.longitude} : {lat:33.421919, lng:-111.940032};
        
        await axios.get(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${loc.lat},${loc.lng}&key=AIzaSyC0gsrJf3UG_brzh_7F0YmHyZK7O2iZZvE`)
          .then((response)=>{
            console.log(response.data.results)
            this.setState(
              {
                address : response.data.results[0].formatted_address,
              }
            )
          }
        )
      }
      handleInput(event){
        const target = event.target;
        this.setState({
          [target.name]: target.value
        });
      }
    
      handleSubmit(event){
        event.preventDefault();
        
        if (this.state.what !== '' && this.state.where !== '') 
          this.props.history.push(`/search?what=${this.state.what}&where=${this.state.where}`);
        else if (this.state.what !== '' && this.state.where === '') 
          this.props.history.push(`/search?what=${this.state.what}&where=${this.state.address}`);
        else if(this.state.what === '' && this.state.where !== '')  
          this.props.history.push(`/search?what=all&where=${this.state.where}`);
        else                                                        
          this.props.history.push(`search?what=all&where=${this.state.address}`);
          
        this.forceUpdate();
        
      }
      
    render() {
     
        return (
          <nav>
            <Link to='/'><img src={IOM} alt="locals-only"/></Link>
            <form onSubmit={this.handleSubmit}>
              <div className="field">
                <div className="control what">
                  <input 
                    placeholder="What to do?" 
                    type="text" 
                    name="what"
                    className="input" 
                    value={this.state.what} 
                    onChange={this.handleInput}
                  />
                </div>
                <div className="control where">
                  <input 
                    placeholder="Where at?"
                    type="text" 
                    name="where"
                    className="input" 
                    value={this.state.where} 
                    onChange={this.handleInput}
                  />
                </div>
                
                <button className="button is-primary search">Submit</button>
              </div>
            </form>
            <Link className="button is-primary create" to="/create-local" > 
              <i>Localize it!</i>
            </Link>
          </nav>
        )
    }
}

export default geolocated({
  positionOptions: {
      enableHighAccuracy: false,
  },
  userDecisionTimeout: 5000,
})(withRouter(Search));
