import React, { Component } from 'react';
import axios from 'axios';
import './Local.scss';
import {TEST} from '../../globals';
import queryString from 'query-string';
export default class Local extends Component {
    constructor(props){
        super(props);
        this.state = {
            id:'',
            items:[],
            loading:true,
            page:[]
        }
    }
    componentDidMount() {
        let id = queryString.parse(this.props.location.search).id;

        this.setState({ 
                id:id,
            }
        ,()=>{this.getData(); })
    }
    getData(){
        axios.get(`${TEST}/${this.state.id}`)
            .then((res)=>{
                    console.log("Postal courier has delivered your package!");
                    const data = res.data;

                    this.setState({ 
                            items:data, 
                            loading: false 
                        }
                    );
                }
            )
            .catch(()=>{console.log("Postal courier has vanished!");});
            this.loadItems();
    }
    loadItems(){
        var item = this.state.items;
        
        this.setState({
            page: [
                <section>
                    <h1>{item.name}</h1>
    
                </section>
            ]
        })
    }
    render() {
        return (
            <div className="local">
                {this.page}
            </div>
        )
    }
}
