import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import './Search.scss'
export default class Search extends Component {
    constructor(props){
        super(props);
        this.state = {
          what: '', 
          where: ''
        };
    
        this.handleChangeWhat = this.handleChangeWhat.bind(this);
        this.handleChangeWhere = this.handleChangeWhere.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
    
      handleChangeWhat(event){
        this.setState({
          what: event.target.value
        });
      }
    
      handleChangeWhere(event){
        this.setState({
          where: event.target.value
        });
      }
    
      handleSubmit(){
        if (this.state.what !== '' && this.state.where !== '') 
            return `/search/${this.state.what}/${this.state.where}`;
        else
                return "/search/all/currentZip";
      }
    render() {
        return (
          <nav>
          <img src='LocalsOnly.png'/>
            <form>
                <div className="field">
                  <label className="label">What</label>
                  <div className="control">
                  <input type="text" className="input"
                value={this.state.what}
                onChange={this.handleChangeWhat}/>
                  </div>
                </div>

                <input type="text" placeholder="where?" 
                id="where-search" 
                value={this.state.where}
                onChange={this.handleChangeWhere}/>

                <Link className="button is-primary" to={this.handleSubmit}>Submit</Link>
            </form>
            </nav>
        )
    }
}
