import React, { Component } from 'react'
import Search from './Components/Search';
import Results from './Pages/Results/Results';
import newLocal from './Pages/newLocal/newLocal';
import Home from './Pages/Home/Home';
import Local from './Pages/Local/Local';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import { geolocated } from "react-geolocated";
import axios from 'axios';
class App extends Component {
  constructor(props) {
    super (props);
    this.state = {
      city:'',
      state:'',
      zip:''
    }
  }
  async componentDidMount() {
    await this.getLocation();
  }
  async getLocation(){
    var loc = await this.props.coords ? {lat:this.props.coords.latitude, lng:this.props.coords.longitude} : {lat:33.421942, lng:-111.940021};

    await axios.get(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${loc.lat},${loc.lng}&key=AIzaSyC0gsrJf3UG_brzh_7F0YmHyZK7O2iZZvE`)
      .then((response)=>{
        this.setState(
          {
            city : response.data.results[0].address_components[1].long_name,
            state : response.data.results[0].address_components[3].long_name,
            zip : response.data.results[0].address_components[5].long_name.str
          }
        )
      }
    )
  }
  render(){
    return (
      <Router>
        <Search city={this.state.city} state={this.state.state} zip={this.state.zip}/>
        <Switch>
          <Route exact path={"/"} component={Home}/>
          <Route path={"/search"} component={Results}/>
          <Route path={"/local"} component={Local}/>
          <Route path={"/create-local"} component={newLocal}/>
        </Switch>
      </Router>
    )
  }
}

export default geolocated({
  positionOptions: {
      enableHighAccuracy: false,
  },
  userDecisionTimeout: 5000,
})(App);
